<script type="text/javascript">

  products_list_functions.push(
    function(){
      if($('#featured-products').parents('.tab-pane').length)
      {   
          if(!$('#featured-products').parents('.tab-pane').hasClass('active'))
          {
              var width_owl_active_tab = $('#featured-products').parents('.tab-pane').siblings('.active').find('.owl-carousel').width();    
              $('#featured-products').width(width_owl_active_tab);
          }
      }
      $('#featured-products').owlCarousel({
                  direction:'ltr',
                items : 4,
        itemsCustom : false,
        itemsDesktop : [1200, 4],
        itemsDesktopSmall : [992, 3],
        itemsTablet : [768, 2],
        itemsTabletSmall : false,
        itemsMobile : [480, 1],
        singleItem : false,         // true : show only 1 item
        itemsScaleUp : false,
        slideSpeed : 200,  //  change speed when drag and drop a item
        paginationSpeed :800, // change speed when go next page

        autoPlay : false,   // time to show each item
        stopOnHover : false,
        navigation : true,
        navigationText : ["&lsaquo;", "&rsaquo;"],

        scrollPerPage :true,
        responsive :true,
        
        pagination : false,
        paginationNumbers : false,
        
        addClassActive : true,
        
        mouseDrag : true,
        touchDrag : true,

        addClassActive :    true,
        afterInit: OwlLoaded,
        afterAction : SetOwlCarouselFirstLast,

      });
    }
  ); 
  function OwlLoaded(el){
    el.removeClass('owl-loading').addClass('owl-loaded').parents('.owl-row').addClass('hide-loading');
    if ($(el).parents('.tab-pane').length && !$(el).parents('.tab-pane').hasClass('active'))
        el.width('100%');
  };
</script><!-- end /var/www/html/themes/ap_office/modules/ps_featuredproducts/views/templates/hook/ps_featuredproducts.tpl --><?php }
